(function() {
  var state = "show";

  var isEditState = function() {    
    return state == "edit";
  };

  this.renderField = function(field) {
    var isEdit = isEditState();
    var type = field.type;
    var fieldId = field.name;
    var fieldName = field.name;    
    var placeholder = field.placeholder ? field.placeholder : "";    

    if (isEdit) {
        placeholder = field.placeholderEdit ? field.placeholderEdit : "";
    }    
    var dataSource = field.dataSource ? field.dataSource : {};
    var html =
      '<div class="form-group ' + (field.required ? "required" : "") + '">';
    html += '<label for="' + field.name + '">' + field.label + "</label>";
    switch (type) {
      case "password":
        //html += '<input type="password" class="form-control form-control-sm" id="' + fieldId + '" name="' + fieldName + '" placeholder="' + placeholder + '">';
        html += '<div class="input-group input-group-sm">';
        html +=
          '<input type="password" class="form-control" id="' +
          fieldId +
          '" name="' +
          fieldName +
          '" placeholder="' +
          placeholder +
          '">';
          
        html += '<div class="input-group-append">';
        html +=
          '<span class="input-group-text"><i class="fa fa-key"></i></span>';
        html += "</div>";
        html += "</div>";

        break;
      case "textarea":
        html +=
          '<textarea rows="5" class="form-control form-control-sm" id="' +
          fieldId +
          '" name="' +
          fieldName +
          '" placeholder="' +
          placeholder +
          '"></textarea>';
        break;
      case "select":
        html +=
          '<select class="form-control input-sm select2" id="' +
          fieldId +
          '" name="' +
          fieldName +
          '" data-placeholder="' +
          placeholder +
          '">';
        for (var key in dataSource) {
          if (dataSource.hasOwnProperty(key)) {
            var value = dataSource[key];
            html += '<option value="' + key + '">' + value + "</option>";
          }
        }
        html += "</select>";
        break;
      default:
        html +=
          '<input type="text" class="form-control form-control-sm" id="' +
          fieldId +
          '" name="' +
          fieldName +
          '" placeholder="' +
          placeholder +
          '">';
    }
    html += "</div>";
    return html;
  };

  this.populateValue = function(record) {
    var options = this.options;
    var fields = options.fields;

    for (var index = 0; index < fields.length; index++) {
      var field = fields[index];
      var fieldName = field.name;
      var type = field.type;
      var value = record[fieldName] ? record[fieldName] : "";
      switch (type) {
        case "select":
          $("#" + fieldName)
            .val(value)
            .trigger("change");
          break;
        default:
          $("#" + fieldName).val(value);
      }
    }
  };

  this.renderForm = function() {
    var options = this.options;
    var className = options.className;
    var fields = options.fields;
    var html = "";
    for (var index = 0; index < fields.length; index++) {
      var field = fields[index];
      html += this.renderField(field);
    }
    $("#modal-form-fields").html(html);

    $(document).find(".select2:enabled").select2({
        theme: "bootstrap4"
    });

    $("#main-modal").modal({
      backdrop: "static",
    });
    $(".modal-dialog").addClass(className);
    $("#main-modal-submit-button").attr(
      "onclick",
      this.name + ".submit(event)"
    );
  };

  this.onShow = function() {
    state = "show";

    var options = this.options;
    options.idValue = this.defaultIdValue;    
    this.renderForm();
    $("#main-modal-submit-button").html('<i class="fa fa-save"></i> Simpan');
  };

  this.onEdit = function(id) {
    state = "edit";

    var self = this;
    var options = this.options;
    options.idValue = id;
    var params = {};
    params.id = id;
    var findUrl = options.findUrl;
    self.state = "edit";

    HELPER.Html.loading();
    SERVER.get(
      findUrl,
      params,
      "json",
      function(data) {
        HELPER.Html.loading(false);

        var status = data.status;
        if (HELPER.Respone.isSuccess(status)) {
          self.renderForm();
          $("#main-modal-submit-button").html(
            '<i class="fa fa-save"></i> Ubah'
          );
          self.populateValue(data.record);
        } else {
          HELPER.Notify.notif(data.status, data.message);
        }
      },
      function(jqXHR, textStatus, errorThrown) {
        HELPER.Html.loading(false);
        HELPER.Notify.failJqhr(jqXHR, textStatus, errorThrown);
      }
    );
  };

  this.submit = function(e) {
    var options = this.options;
    var idValue = options.idValue;
    var params = {};
    params.id = idValue;

    var formDatas = $("#modal-form").serializeArray();
    for (var index = 0; index < formDatas.length; index++) {
      var formData = formDatas[index];
      params[formData.name] = formData.value;
    }
    var saveUrl = options.saveUrl;

    HELPER.Html.loading();
    SERVER.post(
      saveUrl,
      params,
      "json",
      function(data, textStatus, jqXHR) {
        HELPER.Html.loading(false);
        var status = data.status;
        HELPER.Notify.notif(data.status, data.message);
        if (HELPER.Respone.isSuccess(status)) {
          var table = options.table;
          window[table] ? window[table].refresh() : "";
          $("#main-modal").modal("hide");
        }
      },
      function(jqXHR, textStatus, errorThrown) {
        HELPER.Html.loading(false);
        HELPER.Notify.failJqhrReload(jqXHR, textStatus, errorThrown);
      }
    );
  };
}.call(ModalFormBuilder.prototype));

